class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      comments: "",
    };
  }

  handleUserNameChange = (event) => {
    this.setState({ username: event.target.value });
  };

  handleCommentChange = () => {
    this.setState
  }

  render() {
    return (
      <form>
        <div>
          <label>UserName</label>
          <input
            type="text"
            value={this.state.username}
            onChange={this.handleUserNameChange}
          />
        </div>
        <div>
          <label>Comments</label>
          <textarea value={this.state.co} onChange={this.handleCommentChange}></textarea>
        </div>
      </form>
    );
  }
}

export default Form;
